package main

import "fmt"

func main() {
	//fmt.Println(characterReplacement("", 100))
	fmt.Println(characterReplacement("CABAAB", 2))
}

func characterReplacement(s string, k int) int {
	counts := [26]int{}
	start := 0
	maxCharCount := 0
	n := len(s)
	result := 0
	for end := 0; end < n; end++ {
		counts[s[end]-'A']++
		if maxCharCount < counts[s[end]-'A'] {
			maxCharCount = counts[s[end]-'A']
		}
		fmt.Println("1:", end, start, maxCharCount)
		for (end - start - maxCharCount + 1) > k {
			fmt.Println("2", end, start, maxCharCount)
			counts[s[start]-'A']--
			start++
			for i := 0; i < 26; i++ {
				if maxCharCount < counts[i] {
					maxCharCount = counts[i]
				}
			}
		}
		if result < end-start+1 {
			result = end - start + 1
		}
		fmt.Println(counts, result, end, start, maxCharCount)
	}
	return result
}
func characterReplacement1(s string, k int) int {
	if k > len(s)-1 {
		return len(s)
	}
	sl := make([][]int, len(s))
	max := 0
	for i := 0; i < len(s); i++ {
		sl[i] = []int{i, i}
		l := dsp(sl[i], k, s[i], s)
		if l > max {
			max = l
		}
	}
	return max
}

func dsp(s []int, k int, b byte, str string) int {
	//fmt.Println(s, k)
	if k == 0 {
		for s[1] < len(str)-1 && str[s[1]+1] == b {
			s[1]++
		}
		for s[0] > 0 && str[s[0]-1] == b {
			s[0]--
		}
		return s[1] - s[0] + 1
	}
	if s[0] == 0 && s[1] == len(str)-1 {
		return len(str)
	}

	leftS := []int{s[0], s[1]}
	rightS := []int{s[0], s[1]}
	var left, right int
	for rightS[1] < len(str)-1 && str[rightS[1]+1] == b {
		rightS[1]++
	}
	if rightS[1] < len(str)-1 {
		rightS[1]++
		right = dsp(rightS, k-1, b, str)
	} else {
		leftS[1] = rightS[1]
	}

	for leftS[0] > 0 && str[leftS[0]-1] == b {
		leftS[0]--
	}
	if leftS[0] > 0 {
		leftS[0]--
		left = dsp(leftS, k-1, b, str)
	} else {
		left = leftS[1] - leftS[0] + 1
	}

	if left > right {
		return left
	}
	return right
}
